import React, { useState, useEffect } from 'react';
import axios from 'axios';

const QueryButtons = () => {
    const [students, setStudents] = useState([]);
    const [totalPages, setTotalPages] = useState(1);
    const [page, setPage] = useState(1);

    // Fetch students data with pagination
    const fetchStudents = async (pageNumber = 1) => {
        try {
            const response = await axios.get(`http://localhost:3100/api/students?page=${pageNumber}`);
            console.log("Fetched Students:", response.data);

            setStudents(response.data.students);
            setTotalPages(response.data.totalPages);
            setPage(pageNumber);
        } catch (error) {
            console.error("Error fetching students:", error);
            setStudents([]);
        }
    };

    // Fetch students on component mount
    useEffect(() => {
        fetchStudents(1);
    }, []);

    return (
        <div style={{ marginLeft: '20px', maxWidth: '700px' }}>
            <h1>Student Info Database</h1>
            <h2>Students List</h2>

            {students.length > 0 ? (
                <>
                    <table border="1" style={{ width: '100%', marginTop: '20px', textAlign: 'left' }}>
                        <thead>
                            <tr>
                                <th>ID</th>
                                <th>First Name</th>
                                <th>Last Name</th>
                                <th>Address</th>
                            </tr>
                        </thead>
                        <tbody>
                            {students.map((student) => (
                                <tr key={student.id}>
                                    <td>{student.id}</td>
                                    <td>{student.first_name}</td>
                                    <td>{student.last_name}</td>
                                    <td>{student.address}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>

                    {/* Pagination Buttons */}
                    <div style={{ marginTop: '10px', display: 'flex', justifyContent: 'center', gap: '5px' }}>
                        <button disabled={page === 1} onClick={() => fetchStudents(page - 1)}>
                            Previous
                        </button>

                        {[...Array(totalPages)].map((_, index) => (
                            <button
                                key={index + 1}
                                onClick={() => fetchStudents(index + 1)}
                                style={{ fontWeight: page === index + 1 ? 'bold' : 'normal' }}
                            >
                                {index + 1}
                            </button>
                        ))}

                        <button disabled={page === totalPages} onClick={() => fetchStudents(page + 1)}>
                            Next
                        </button>
                    </div>
                </>
            ) : (
                <p>No data available</p>
            )}
        </div>
    );
};

export default QueryButtons;
