import React, { useState } from 'react';
import axios from 'axios';

const QueryButtons = () => {
    const [data, setData] = useState([]);
    const [columns, setColumns] = useState([]);
    const [title, setTitle] = useState('Query Results');
    const [page, setPage] = useState(1); // Pagination state
    const [totalPages, setTotalPages] = useState(1); // Store total pages

    // Function to fetch data from API
    const fetchData = async (endpoint, title, pageNumber = 1) => {
        try {
            console.log(`Fetching data from: ${endpoint}, Page: ${pageNumber}`);
            const response = await axios.get(`http://localhost:3100/api/${endpoint}?page=${pageNumber}`);

            let results = response.data;

            // If fetching students, extract the `students` array and pagination info
            if (endpoint === 'students') {
                results = response.data.students;
                setTotalPages(response.data.totalPages); // Set total pages
            }

            if (results.length > 0) {
                setColumns(Object.keys(results[0])); // Extract column names dynamically
            }

            setData(results);
            setTitle(title);
            setPage(pageNumber);
        } catch (error) {
            console.error(`Error fetching ${endpoint}:`, error);
            setData([]);
            setColumns([]);
            setTitle(`Error loading ${title}`);
        }
    };

    return (
        <div style={{ marginLeft: '20px' }}>
            <h1>List All the Records</h1>
            <button onClick={() => fetchData('departments', 'Departments')}>
                List All Departments
            </button>
            <button onClick={() => fetchData('courses', 'Courses')}>
                List All Courses
            </button>
            <button onClick={() => fetchData('students', 'Students', 1)}>
                List All Students
            </button>

            <h2>{title}</h2>
            {data.length > 0 ? (
                <>
                    <table border="1" style={{ width: '100%', marginTop: '20px', textAlign: 'left' }}>
                        <thead>
                            <tr>
                                {columns.map((col, index) => (
                                    <th key={index}>{col}</th>
                                ))}
                            </tr>
                        </thead>
                        <tbody>
                            {data.map((row, rowIndex) => (
                                <tr key={rowIndex}>
                                    {columns.map((col, colIndex) => (
                                        <td key={colIndex}>
                                            {/* Handle courses as an array */}
                                            {Array.isArray(row[col]) ? (
                                                row[col].map((course, i) => (
                                                    <a key={i} href={course.url} style={{ display: 'block' }}>
                                                        {course.name}
                                                    </a>
                                                ))
                                            ) : (
                                                row[col] // Render normal text fields
                                            )}
                                        </td>
                                    ))}
                                </tr>
                            ))}
                        </tbody>
                    </table>

                    {/* Pagination Controls (Only for Students) */}
                    {title === 'Students' && (
                        <div style={{ marginTop: '10px' }}>
                            <button 
                                disabled={page === 1} 
                                onClick={() => fetchData('students', 'Students', page - 1)}
                            >
                                Previous
                            </button>
                            <span style={{ margin: '0 10px' }}> Page {page} of {totalPages} </span>
                            <button 
                                disabled={page === totalPages} 
                                onClick={() => fetchData('students', 'Students', page + 1)}
                            >
                                Next
                            </button>
                        </div>
                    )}
                </>
            ) : (
                <p>No data available</p>
            )}
        </div>
    );
};

export default QueryButtons;
